package com.decard.zj.founctiontest;

import android.graphics.Bitmap;
import android.graphics.Canvas;
import android.graphics.Color;
import android.graphics.Matrix;
import android.graphics.Paint;
import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.util.Base64;
import android.widget.ImageView;

import com.example.commonlibs.utils.BitmapUtils;

public class BitmapActivity extends AppCompatActivity {

    String backgroud = "";

    String foreground = "";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_bitmap);
        byte[] bytes1 = Base64.decode(foreground, 0);
        Bitmap bitmap1 = BitmapUtils.bytesToBitmap(bytes1);
        byte[] bytes2 = Base64.decode(foreground, 0);
        Bitmap bitmap2 = BitmapUtils.bytesToBitmap(bytes2);
        Bitmap bitmap = dealBitmap(bitmap2);
        ImageView imageView = findViewById(R.id.iv_bitmap);
        imageView.setImageBitmap(combineBitmap(bitmap1, bitmap));
    }

    private Bitmap dealBitmap(Bitmap bitmap) {
        Bitmap whiteBgBitmap = Bitmap.createBitmap(bitmap.getWidth(), bitmap.getHeight(), Bitmap.Config.ARGB_8888);
        Canvas canvas = new Canvas(whiteBgBitmap);
        canvas.drawColor(Color.TRANSPARENT);
        canvas.drawBitmap(bitmap, 0, 0, null);
        return whiteBgBitmap;
    }

    public static Bitmap combineBitmap(Bitmap background, Bitmap foreground) {
        if (background == null) {
            return null;
        }
        int bgWidth = background.getWidth();
        int bgHeight = background.getHeight();
        Bitmap newmap = Bitmap
                .createBitmap(bgWidth, bgHeight, Bitmap.Config.ARGB_8888);
        Canvas canvas = new Canvas(newmap);
        Paint paint = new Paint();
        canvas.drawBitmap(background, new Matrix(), null);
        canvas.drawBitmap(foreground, 0, 0, paint);
        canvas.save();
        canvas.restore();
        return newmap;
    }

    private Bitmap mergeThumbnailBitmap(Bitmap b1, Bitmap b2) {
        if (!b2.isMutable()) {
            //设置图片为背景为透明
            b2 = b2.copy(Bitmap.Config.ARGB_8888, true);
        }
        Paint paint = new Paint();

        Canvas canvas = new Canvas(b1);
        canvas.drawBitmap(b2, 0, 0, paint);//叠加新图b2
        canvas.save();
        canvas.restore();
        return b2;
    }
}
